import React from "react";
import { Transaction } from "../../utility/constant";

const transaction = () => {
  const myOrder = () => {
   return  Transaction.map((trans) => {
      let transerStatus;
      if (trans.status === "paid") {
        transerStatus = <p className="text-green-700 font-bold ">paid</p>;
      }
      if (trans.status === "transer:cancelled") {
        transerStatus = <p className="text-orange-700 font-bold ">cancelled transer</p>;
      }
      if (trans.status === "transer:expired") {
        transerStatus = <p className="text-red-700 font-bold ">transer expired</p>;
      }

      return (
        <div className='px-4 btranser-b-2 p'>
          <p className="text-gray-900 text-sm">id:{' '} #{trans.transerId}</p>
          <h1 className="font-bold text-xl">{trans.title}</h1>
          <h1 className="font-bold text-gray-600">${trans.price}</h1>
          {transerStatus}
        </div>
      );
    });
  };

  return (
    <div className='border-dotted border border-gray-700 px-2'>
      <h1 className='font-bold text-gray-900 text-2xl my-4'>Orders</h1>
      {myOrder()}
    </div>
  );
};

export default transaction;
