import { Product } from "../../utility/constant";
const view = ({ prod }) => {
  const {
    image,
    title,
    price,
    description,
    state,
    name,
    brand,
    location,
    contact,
  } = prod;

  const details = () => {1
    return (
      <div className='grid-2  '>
        <div className=' p-12 sm:p-4'>
          <img src='../../7eleven.jpg' alt='' srcset='' />
        </div>
        <div className='pt-4'>
          <div className="pb-4">
            <span className="font-semibold text-xl">
              Name: <span className="text-xl font-bold text-gray-800">{title}</span>
            </span>
          </div>

          <div className='pb-4'>
            <span className="font-semibold text-xl">
              price: <span className="text-sm font-bold text-gray-800">${price}</span>
            </span>
          </div>
          <div className=''>
            <span className="font-semibold text-xl">
              description: <span className="text-sm font-bold text-gray-600">{description}</span>
            </span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className='flex flex-col justify-center items-center'>
      <div className=' max-w-6xl w-full bg-gray-600 my-16 h-screen flex flex-col justify-center items-center'>
        <p className='text-4xl mx-2 font-bold'>Product Details</p>
        <div className='grid-2 bg-green-500 w-full'>
          <div className=' border-r border-gray-500'>{details()}</div>

          <div className=''>seller profile</div>
        </div>
      </div>
    </div>
  );
};

view.getInitialProps = (client) => {
  const prod = Product.find((prod) => prod.id === client.query.view);

  return { prod };
};

export default view;
