import React from "react";
import { Upload, Button, message } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import Link from 'next/link'

const New = () => {
  const fileList = [];

  const onChange = (info) => {
    if (info.file.status !== "uploading") {
      console.log(info.file, info.fileList);
    }
    if (info.file.status === "done") {
      //do some upload to cloudinary
      // return url add to array list of images
      // upload data to database
      message.success(`${info.file.name} file uploaded successfully`);
    } else if (info.file.status === "error") {
      message.error(`${info.file.name} file upload failed.`);
    }
  };

  const props = {
    listType: "picture",
    defaultFileList: [...fileList],
  };
  return (
    <div className='flex flex-col justify-center items-center '>
      <div className='max-w-6xl  w-full my-16 py-16 flex flex-col justify-center items-center'>
        <h1 className="text-2xl font-bold text-gray-600">Create product</h1>
        <form className=" max-w-2xl border border-gray-500 p-12 ">
          <Upload {...props} onChange={onChange}>
            <Button>
              <UploadOutlined /> Upload
            </Button>
          </Upload>
          <div className='mt-4'>
            <label htmlFor='email' className='text-gray-500 font-bold'>
              Name
            </label>
            <input
              type='text'
              placeholder='product name'
              className=' focus:bg-white rounded bg-gray-300 shadow-lg border leading-tight focus:outline-none  appearance-none'
            />
          </div>

          <div className='mt-4'>
            <label htmlFor='email' className='text-gray-500 font-bold'>
              Price
            </label>
            <input
              type='email'
              placeholder='price'
              className=' focus:bg-white rounded bg-gray-300 shadow-lg border leading-tight focus:outline-none  appearance-none'
            />
          </div>

          <div className='mt-4'>
            <label htmlFor='email' className='text-gray-500 font-bold'>
              Desc;
            </label>
            <input
              type='text'
              placeholder='product descriptions'
              className=' focus:bg-white rounded bg-gray-300 shadow-lg border leading-tight focus:outline-none  appearance-none'
            />
          </div>

          <button type="submit" className="bg-blue-700 rounded-lg p text-white mt-4">submit</button> {' '}
          <Link href="product/"><a className="bg-red-500 rounded-lg p text-white mt-4">home</a></Link>
        </form>
      </div>
    </div>
  );
};

export default New;
