import React from "react";
import { Product } from "../../utility/constant";
const index = () => {
  const products = () => {
    return Product.map((prod) => {
      let status;
      let description;
      if (prod.state === "created") {
        status = <p className='text-orange-700 font-bold'>not sold</p>;
      }
      if (prod.state === "sold") {
        status = <p className='text-green-700 font-bold'>Already sold</p>;
      }
        if (prod.description.length >=200) {
            description = prod.description.substring(0,200)+"..."
        }else{
            description = prod.description
        }

      return (
        <div className='flex flex-col  border border-gray-400 shadow-lg mx-4 sm:mx-0'>
          <img src='../7eleven.jpg' alt='product list ' className="" />
          <p className="font-bold my-4 text-xl capitalize  mx-2">{prod.title}</p>
          <p className="text-gray-700 px-4 text-center mb-4">{description}</p>
          <div className='flex  justify-evenly w-full '>
            <p className="font-bold">${prod.price}</p>
            {status}
          </div>
          <div className='flex justify-evenly items-center w-full my-4'>
      <a href='' className="font-semibold">{prod.state!=="sold"&& "Order"}</a>
            <a href='' className="font-semibold">View</a>
          </div>
        </div>
      );
    });
  };

  return (
    <div className=' flex flex-col justify-center items-center '>
      <div
        className=' w-full h-auto py-32 object-cover bg-fixed flex flex-col justify-center items-center '
        style={{ backgroundImage: "url(../../shop.jpg)" }}
      >
        <div className=' max-w-lg '>
          <p className='font-extrabold text-white text-2xl'>local market</p>
          <p className='text-white'>
            Get your product and leave the transaction with us{" "}
          </p>
          <form>
            <input
              type='text'
              placeholder='search for your desire product'
              className='bg-white  rounded focus:outline-none'
            />
          </form>
        </div>
      </div>
      <div className=' max-w-6xl w-full my-10'>
        <div className='grid-3'>{products()}</div>
      </div>
    </div>
  );
};

export default index;
