import React from 'react'
import {Product} from '../../utility/constant'
const view = (prod) => {
    const {image,title,price,description,state,name,brand,location,contact} = prod

    const details =()=>{
       return <div className="grid-2 justify-center items-center ">
            <div className=" p-4">
                <img src="../../7eleven.jpg" alt="" srcset=""/>
            </div>
            <div className="">details</div>
        </div>
    }

    return (
        <div className="flex flex-col justify-center items-center">
         <div className=" max-w-6xl w-full bg-gray-600 my-16 h-screen flex flex-col justify-center items-center">
       <div className="grid-2 bg-green-500 w-full">
          <div className=" border-r border-gray-500">
              <p className="text-4xl mx-2 font-bold">Product Details</p>
              {details()}
          </div>

          <div className="">seller profile</div>
       </div>
         </div>
        </div>
    )
}

view.getInitialProps = (client)=>{

  const prod = Product.find((prod)=>prod.id === client.query.view)


  return{prod}

}

export default view
