import React from "react";
import { product } from "../../utility/constant";
const profile = () => {
  const myProduct = () => {
    return product.map((prd) => {
      let prdStatus;
      if (prd.productStatus === "paid") {
        prdStatus = <p className='text-green-500  font-bold p '>sold</p>;
      }

      if (prd.productStatus === "awaiting:payment") {
        prdStatus = (
          <p className='text-orange-500 font-bold p'>payment in progress</p>
        );
      }

      return (
        <div>
          <div className=' m-w-sm overflow-hidden shadow-lg'>
            <img className='w-full' src='../../7eleven.jpg' alt='product' />
            <div className='px-4 py-4'>
              <h1 className='font-bold'>{prd.title}</h1>
              <p className='text-sm text-gray-600 text-center'>
                {prd.description}
              </p>
            </div>
            <div className='flex justify-evenly items-center'>
              <p className='font-bold text-purple-800'>${prd.price}</p>
              {prdStatus}
            </div>
            <div className='flex justify-evenly py-4'>
              <a
                href=''
                className='bg-blue-600 rounded-full px-2 text-white text-sm hover:bg-blue-400'
              >
                update
              </a>
              <a
                href=''
                className='bg-red-600 rounded-full px-2 text-white text-sm hover:bg-red-400'
              >
                delete
              </a>
            </div>
          </div>
        </div>
      );
    });
  };

  return (
    <div className='flex flex-col px-6 border-dotted border border-gray-600'>
      <h1 className='font-bold text-gray-900 text-2xl my-4'>profile</h1>
      <div className='flex justify-between  '>
        <div className=''>
          <div className='flex mb-4'>
            <h1 className='pr-4 font-bold text-gray-700'>username:</h1>
            <p className='text-gray-900'>Alex milla</p>
          </div>

          <div className='flex mb-4'>
            <h1 className='pr-4 font-bold text-gray-700'>number:</h1>
            <p className='text-gray-900'>081 3333 333 33</p>
          </div>

          <div className='flex mb-4'>
            <h1 className='pr-4 font-bold text-gray-700'>Brand:</h1>
            <p className='text-gray-900'>Nikki stores</p>
          </div>

          <div className='flex mb-4'>
            <h1 className='pr-4 font-bold text-gray-700'>Location:</h1>
            <p className='text-gray-900'>Ikeja Lagos</p>
          </div>
          <a href="" className="bg-green-700 text-white rounded-lg font-bold hover:bg-green-600">Edit profile</a>
        </div>
        <div className=''>
          <img src='../../logo.png' alt='' className="w-42 boor"/>
        </div>
      </div>

      <div className=' pb-20'>
        <h1 className='font-bold text-gray-900 text-2xl my-4'>my products</h1>
        <div className='grid-3'>{myProduct()}</div>
      </div>
    </div>
  );
};

export default profile;
