import React, { useRef, useState, useEffect } from "react";
import {
  PaystackButton,
  PaystackConsumer,
  usePaystackPayment,
} from "react-paystack";

const orderedProduct = {
  orderId: "rrhffhdhggevffhk40",
  status: "order:created",
  title: "lois V",
  price: 500,
};

const Payment = () => {
  const [timerhours, settimerhours] = useState("00");
  const [timerminute, settimerminute] = useState("00");
  const [timerseconds, settimerseconds] = useState("00");

  //date in future
  const time = new Date("june 14, 2020 10:44");

  const distance = new Date(time) - new Date();

  let interval;
  console.log(interval);

  const startTimer = () => {
    interval = setInterval(callback, 1000);
  };

  const callback = () => {
    const hours = Math.floor(
      (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
    );
    const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((distance % (1000 * 60)) / 1000);

    if (interval < 0) {
      clearInterval(interval);
    } else {
      settimerhours(hours);
      settimerminute(minutes);
      settimerseconds(seconds);
    }
  };

  //useEffect
  useEffect(() => {
    startTimer();
    return () => {
      clearInterval(interval);
    };
  });

  if (timerminute <= 0) {
    clearInterval(interval);
    return (
      <div className='flex flex-col justify-center items-center'>
        <p className='bg-red-700 text-white font-bold p w-full text-center '>
          payment Expired
        </p>
        <img
          src='../sad.svg'
          className=' w-16 sm:w-32 text-gray-600'
          alt=''
          srcset=''
        />
        <button className='bg-blue-700 rounded p text-white my-2'>Home</button>
      </div>
    );
  }
  //order product display fuction
  const order = () => {
    //paystack props
    const componentProps = {
      ...config,
      text: "Paystack Button Implementation",
      email: "tohshine@gmail.com",
      onSuccess: (res) => console.log(res),
      onClose: () => null,
    };

    //paystack configuration
    const config = {


      email: "tohshine@gmail.com",
      amount: (orderedProduct.price),
      currency: "NGN",
      publicKey: "pk_test_c61416a3fbe75b46a24de076ee2dfe6acbbe474e",
      metadata: {
        custom_fields: [
          {
            display_name: "tosin owoeye",
            variable_name: "08138385529",
            value: "08138385529",
          },
        ],
      },
    };

    return (
      <div className='grid-2  '>
        <div className=' p-4'>
          <img src='../../7eleven.jpg' alt='local market product details' />
        </div>

        <div className='pt-4'>
          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              Name:{" "}
              <span className='text-xl font-bold text-gray-800 mx-4'>
                {orderedProduct.title}
              </span>
            </span>
          </div>

          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              price:{" "}
              <span className='text-sm font-bold text-gray-800 mx-4'>
                ${orderedProduct.price}
              </span>
            </span>
          </div>

          <div className=''>
            <span className='font-semibold text-xlpx-4'>
              Description:{" "}
              <span className='text-sm font-bold text-gray-600 mx-4'>
                {orderedProduct.description}
              </span>
            </span>
          </div>

          <PaystackConsumer {...componentProps}>
            {({ initializePayment }) => (
              <button onClick={() => initializePayment()}>Pay</button>
            )}
          </PaystackConsumer>
        </div>
      </div>
    );
  };

  return (
    <div className='flex flex-col justify-center items-center'>
      <div className='max-w-6xl w-full bg-white mt-12'>
        <h1 className='text-gray-800 text-lg font-bold text-right m-8'>
          {`${timerhours}H:${timerminute}M:${timerseconds}S`}
        </h1>
        {order()}
      </div>
    </div>
  );
};

export default Payment;
