import React, { useState, useEffect } from "react";

const payment = () => {
  var countDownDate = new Date("Jan 5, 2021 15:37:25").getTime();
  const [timer, settimer] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

  useEffect(() => {
    const setTimer = () => {
      // Get today's date and time
      var now = new Date().getTime();

      // Find the distance between now and the count down date
      var distance = countDownDate - now;

      // Time calculations for days, hours, minutes and seconds
      settimer({
        ...timer,
     days: Math.floor(distance / (1000 * 60 * 60 * 24)),
      });
      settimer({
        ...timer,
        hours: Math.floor(
          (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
        ),
      });
      settimer({
        ...timer,
        minutes: Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
      });
      settimer({
        ...timer,
        seconds: Math.floor((distance % (1000 * 60)) / 1000),
      });
    };
    const timerId = setInterval(setTimer, 1000);
    return () => {
      // doing some cleanup cancelling order automatically
    };
  }, [countDownDate]);
  //order product display fuction
  const orderedData = () => {};

  return (
    <div className='flex flex-col justify-center items-center'>
      <div className='max-w-6xl w-full bg-white mt-12'>
        <h1 className='text-gray-800 text-lg font-bold text-right m-8'>
          {`${timer.days}:${timer.hours}:${timer.minutes}:${timer.seconds}`}
        </h1>
      </div>
    </div>
  );
};

export default payment;
