     return product.map(prod=>{
         let status;
         if (prod.status === "created") {
             status = <p className="text-green-700">sold</p>
         }
     })
    }




  return (
    <div className=' flex flex-col justify-center items-center'>
      <div
        className=' w-full h-auto py-32 object-cover bg-fixed flex flex-col justify-center items-center '
        style={{ backgroundImage: "url(../../shop.jpg)" }}
      >
       <div className=" max-w-lg ">
       <p className='font-extrabold text-white text-2xl'>local market</p>
        <p className="text-white">Get your product and leave the transaction with us </p>
