          <input
            type='text'
            placeholder='search for your desire product'
            className='bg-white  rounded focus:outline-none'
          />
        </form>
       </div>
      </div>
      <div className=' max-w-6xl w-full my-10'>all product</div>
    </div>
  );
};

export default index;
