import React, { useState } from "react";
import Profile from "../../component/profile/profile";
import Order from "../../component/profile/order";
import Notification from "../../component/profile/notification";
import Transaction from "../../component/profile/transaction";

const Index = () => {
  const [pagecount, setpagecount] = useState(1);

  const changePage = () => {
    setpagecount(pagecount + 1);
  };

  const displayPage = () => {
    switch (pagecount) {
      case 1:

        return <Profile />;

      case 2:

        return <Order />;

      case 3:

        return <Transaction />;

      case 4:

        return <Notification />;

      default:
        return setpagecount(1);
    }
  };

  return (
    <div className='flex-col flex justify-center items-center'>
      <div className=' max-w-6xl w-full mt-16'>
        <a className='bg-red-400 text-white p rounded font-bold ml-2 sm:ml-0'>
          Home
        </a>
        <div className=' grid-2-5 mt-4 border-dotted border border-gray-600 p'>
          <div className='  flex flex-col justify-center items-center '>
            <div className='m-2'>
              <img
                src='../logo.png'
                alt='profile picture'
                className='rounded border'
              />
            </div>
            <a
              href=''
              className='bg-white border-2 sm:w-full p text-center rounded hover:bg-green-300 hover:text-white '
            >
              Edit profile
            </a>
            <a
              href=''
              className='bg-red-400 border-2 sm:w-full p text-center rounded hover:bg-red-300 hover:text-white mt-2 text-gray-600'
            >
              Logout
            </a>
          </div>
          <div className=''>
            <div className='flex justify-evenly items-center bg-gray-900 p m rounded'>

              <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize'
                onClick={ setpagecount(1)}
              >
                profile
              </a>
               <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize'
                onClick={pagecount===2 && setpagecount(2)}
              >
                order
              </a>
              <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize'
                onClick={pagecount===2 && setpagecount(3)}
              >
                transactions
              </a>
              <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize'
                onClick={ pagecount===4 && setpagecount(4)}
              >
                notification
              </a>
            </div>

              {displayPage()}

          </div>
        </div>
      </div>
    </div>
  );
};

export default Index;
