import React from "react";
import styled from "styled-components";
import Link from 'next/link'

const Ul = styled.ul`
  list-style: none;
  display: flex;
  flex-flow: row nowrap;

  li {
    padding: 18px 10px;
  }

  @media (max-width: 768px) {
    flex-flow: column nowrap;
    background-color: #0d2538;
    position: fixed;
    transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
    top: 0;
    right: 0;
    height: 100vh;
    width: 300px;
    padding-top: 3.5rem;
    transition: transform 0.3s ease-in-out;

    li {
      color: #fff;
    }
  }
`;

const RightNav = ({ open }) => {
  return (
    <Ul open={open} className=' z-30 px-5 text-xl font-bold capitalize'>
      <li>
        <a href='auth/signin' className='text-green-200'>
          signin
        </a>
      </li>
      <li>
        <a href='auth/signup' className='text-green-200'>
          {" "}
          signup{" "}
        </a>
      </li>
      <li>
        <a href='profile' className='text-green-200'>
          profile
        </a>
      </li>
      <li>
        <a href='' className='text-green-200'>
          logout
        </a>
      </li>
    </Ul>
  );
};

export default RightNav;
