import React from "react";
import { Transaction } from "../../utility/constant";

const transaction = () => {
  const myOrder = () => {
   return  order.map((ord) => {
      let orderStatus;
      if (ord.status === "paid") {
        orderStatus = <p className="text-green-700 font-bold ">paid</p>;
      }
      if (ord.status === "order:cancelled") {
        orderStatus = <p className="text-orange-700 font-bold ">cancelled order</p>;
      }
      if (ord.status === "order:expired") {
        orderStatus = <p className="text-red-700 font-bold ">order expired</p>;
      }

      return (
        <div className='px-4 border-b-2 p'>
          <p className="text-gray-900 text-sm">id:{' '} #{ord.orderId}</p>
          <h1 className="font-bold text-xl">{ord.title}</h1>
          <h1 className="font-bold text-gray-600">${ord.price}</h1>
          {orderStatus}
        </div>
      );
    });
  };

  return (
    <div className='border-dotted border border-gray-700 px-2'>
      <h1 className='font-bold text-gray-900 text-2xl my-4'>Orders</h1>
      {myOrder()}
    </div>
  );
};

export default transaction;
