import React, { useState } from "react";
import Profile from "../../component/profile/profile";
import Order from "../../component/profile/order";
import Notification from "../../component/profile/notification";
import Transaction from "../../component/profile/transaction";

const Index = () => {
  const [pagecount, setpagecount] = useState(1);



  const displayPage = () => {
    switch (pagecount) {
      case 1:
        return <Profile />;

      case 2:
        return <Order />;

      case 3:
        return <Transaction />;

      case 4:
        return <Notification />;

      default:
        return setpagecount(1);
    }
  };

  return (
    <div className='flex-col flex justify-center items-center'>
      <div className=' max-w-6xl w-full mt-16'>
        <a className='bg-red-400 text-white p rounded font-bold ml-2 sm:ml-0'>
          Home
        </a>
        <div className=' grid-2-5 mt-4 border-dotted border border-gray-600 p shadow-lg mx-2 sm:mx-0 bg-gray-100'>
          <div className='  flex flex-col '>
            <div className='m-2'>
              <img
                src='../logo.png'
                alt='profile picture'
                className='rounded-full sm:rounded w-24 sm:w-32 border'
              />
            </div>

          </div>
          <div className=''>
            <div className='flex justify-evenly items-center bg-gray-900 p m rounded'>
              <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize active:bg-orange-700'
                onClick={()=>setpagecount(1)}
              >
                profile
              </a>
              <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize'
                onClick={()=>setpagecount(2)}
              >
                order
              </a>
              <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize'
                onClick={()=>setpagecount(3)}
              >
                transactions
              </a>
              <a
                href='#!'
                className='font-bold text-gray-300  text-sm hover:text-orange-500 capitalize'
                onClick={()=>setpagecount(4)}
              >
                notification
              </a>
            </div>

            {displayPage()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Index;
