import React from "react";
import styled from "styled-components";

const Ul = styled.ul`
  list-style: none;
  display: flex;
  flex-flow: row nowrap;

  li {
    padding: 18px 10px;
  }

  @media (max-width: 768px) {
    flex-flow: column nowrap;
    background-color: #0d2538;
    position: fixed;
    transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
    top: 0;
    right: 0;
    height: 100vh;
    width: 300px;
    padding-top: 3.5rem;
    transition: transform 0.3s ease-in-out;

    li {
      color: #fff;
    }
  }
`;

const RightNav = ({ open }) => {
  return (
    <Ul open={open} className=" z-30 px-5 text-xl font-bold">
      <li href='' className='text-green-200'>
        <a href="" className>signin</a>
      </li>
      <li href='' className='text-green-200'>
        signup
      </li>
      <li href='' className='text-green-200'>
        order
      </li>
      <li href='' className='text-green-200'>
        profile
      </li>
      <li href='' className='text-green-200'>
        logout
      </li>
      <p className="text-sm">&copy; local market 2020</p>
    </Ul>
  );
};

export default RightNav;
