import React from "react";
import { product } from "../../utility/constant";
const profile = () => {
  const myProduct = () => {
    return product.map((prd) => (
      <div>
        <div className=' m-w-sm overflow-hidden shadow-lg'>
          <img src='../../logo.png' alt='product' />
          <div className='px-2'>
            <h1 className="font-bold">{prd.title}</h1>
            <p className="text-sm text-gray-600">{prd.description}</p>

          </div>
          <div className='flex justify-evenly py-4'>
            <a href='' className="bg-blue-600 rounded-full px-2 text-white text-sm hover:bg-blue-400">update</a>
            <a href='' className="bg-red-600 rounded-full px-2 text-white text-sm hover:bg-red-400">delete</a>
          </div>
        </div>
      </div>
    ));
  };

  return (
    <div className='flex flex-col px-6 border-dotted border border-gray-600'>
      <h1 className='font-bold text-gray-900 text-2xl my-4'>profile</h1>
      <div className=' '>
        <div className='flex mb-4'>
          <h1 className='pr-4 font-bold text-gray-700'>username:</h1>
          <p className='text-gray-900'>Alex milla</p>
        </div>

        <div className='flex mb-4'>
          <h1 className='pr-4 font-bold text-gray-700'>number:</h1>
          <p className='text-gray-900'>081 3333 333 33</p>
        </div>

        <div className='flex mb-4'>
          <h1 className='pr-4 font-bold text-gray-700'>Brand:</h1>
          <p className='text-gray-900'>Nikki stores</p>
        </div>

        <div className='flex mb-4'>
          <h1 className='pr-4 font-bold text-gray-700'>Location:</h1>
          <p className='text-gray-900'>Ikeja Lagos</p>
        </div>
      </div>

      <div className=' pb-20'>
        <h1 className='font-bold text-gray-900 text-2xl'>my products</h1>
        <div className='grid-4'>
            {myProduct()}
        </div>
      </div>
    </div>
  );
};

export default profile;
