import React, { useRef, useState, useEffect } from "react";




const Payment = () => {

//date in future
const timeInFuture = new Date().getTime() + 900000;

//current time
const now = new Date().getTime();

//calculate distance
const distance = timeInFuture - now;

const interval = useRef();

const startTimer = () => {
  interval = setInterval(callback, 1000);
};

const callback = () => {
  const hours = Math.floor(
    (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
  );
  const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  const seconds = Math.floor((distance % (1000 * 60)) / 1000);

  if (interval <= 0) {
    clearInterval(interval.current);
  } else {
    settimerhours(hours);
    settimerminute(minutes);
    settimerseconds(seconds);
  }
};

//useEffect
useEffect(() => {
  startTimer()
  return () => {
    clearInterval(interval.current)
  }
})


  //order product display fuction
  const orderedData = () => {};

  return (
    <div className='flex flex-col justify-center items-center'>
      <div className='max-w-6xl w-full bg-white mt-12'>
        <h1 className='text-gray-800 text-lg font-bold text-right m-8'>
          {`${timerhours}H:${timerminute}M:${timerseconds}S`}
        </h1>
      </div>
    </div>
  );
};

export default Payment;
