import React from "react";
import { about, testimony } from "../utility/constant";

const index = () => {
  const About = () => {
    return about.map((abt) => (
      <div className='h-full  rounded-lg select-none '>
        <div className=' select-none flex flex-col justify-center items-center py-2 rounded-lg'>
          <img
            src={abt.image}
            className='sm:h-24 w-24 rounded-full border-2 border-red-500 h-22 w-20'
            alt='local market logo'
          />

          <h1 className=' text-gray-900 text-2xl sm:text-3xl font-extrabold my-2'>
            {abt.title}
          </h1>
          <p className='px-2 text-center text-gray-600'>{abt.description}</p>
        </div>
      </div>
    ));
  };

  const Testimony = () => {
    return testimony.map((tst) => (
      <div className=' select-none h-full rounded-lg bg-white flex-col flex justify-center items-center py-2 '>
        <img
          src={tst.image}
          className='sm:h-24 sm:w-24 h-22 w-20 rounded-full border '
          alt='local market logo'
        />

        <h1 className='text-gray-900 font-bold sm:text-2xl py-4 '>
          {tst.name}
        </h1>
        <p className=' px-4 font-semibold text-center  text-gray-700'>{tst.description}</p>
        <p className='italic text-base text-gray-600'>~{tst.role}</p>
      </div>
    ));
  };

  return (
    <div>
      {/* About section */}
      <div className=' flex flex-col  justify-center items-center py-6 sm:py-40'>
        <div className=' max-w-6xl w-full'>
          <h1 className='text-center select-none text-gray-900 text-2xl  sm:text-6xl font-bold'>
            How it works?
          </h1>
          <div className=' grid-3 px-2'>{About()}</div>
        </div>
      </div>

      {/* Testimony section */}

      <div className=' select-none flex flex-col justify-center items-center bg-gray-900 pb-20 border-t-8 border-red-400 '>
        <div className=' w-full max-w-6xl px-2 sm:px-2'>
          <div className='relative py-4'>
            <h1 className=' select-none text-xl sm:text-3xl text-gray-500 font-bold '>
              Testimonies
            </h1>
            <div className='bg-gray-300 m absolute rounded-full items-center justify-center flex h-32 w-32 border-8  border-red-400 invisible md:visible right-0 bottom-0'>
              <img
                src='./logo.png'
                className='h-24 w-24 rounded-full '
                alt='local market logo'
              />
            </div>
          </div>

          <div className=' grid-3 py-2'>{Testimony()}</div>
        </div>
        <div className="py-6">
          <div className=" grid-4">
            <div className="text-gray-400">
              img
            </div>
            <div className="text-gray-400">address</div>
            <div className="text-gray-400">location</div>
            <div className="text-gray-400">contact</div>
          </div>
          <div className="text-gray-400 text-center py-4">&copy; local market 2020</div>
        </div>
      </div>
    </div>
  );
};

export default index;
