import React from "react";
import { Product } from "../../utility/constant";
const view = ({ prod }) => {
  const {
    image,
    title,
    price,
    description,
    state,
    name,
    brand,
    location,
    contact,
  } = prod;

  const details = () => {
    let productState;
    if (state === "created") {
      productState = "available";
    }

    if (state === "awaiting:payment") {
      productState = "in purchase";
    }

    return (
      <div className='grid-2  '>
        <div className=' p-12 sm:p-4'>
          <img src='../../7eleven.jpg' alt='local market product details' />

         <div className="mt-8">
         <a href="" className="bg-blue-600 text-white font-bold p-4 rounded-lg block text-center hover:bg-blue-400">Order</a>
         </div>
        </div>
        <div className='pt-4'>
          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              Name:{" "}
              <span className='text-xl font-bold text-gray-800 mx-4'>{title}</span>
            </span>
          </div>

          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              price:{" "}
              <span className='text-sm font-bold text-gray-800 mx-4'>${price}</span>
            </span>
          </div>

          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              State:{" "}
              <span
                className={
                  state === "created"
                    ? " bg-green-700 text-white p  rounded-full text-sm mx-4"
                    : "bg-orange-700 text-white p  rounded-full text-sm mx-4"
                }
              >
                {productState}
              </span>
            </span>
          </div>
          <div className=''>
            <span className='font-semibold text-xl'>
              description:{" "}
              <span className='text-sm font-bold text-gray-600 mx-4'>
                {description}
              </span>
            </span>
          </div>

        </div>
      </div>
    );
  };

  const sellerDetails = () => {
    return (
      <div className=''>
        <div className='pt-2'>
          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              Name:{" "}
              <span className='text-xl font-bold text-gray-800 mx-4'>{name}</span>
            </span>
          </div>

          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              Brand:{" "}
              <span className='text-xl font-bold text-gray-600 mx-4'>{brand}</span>
            </span>
          </div>

          <div className='pb-4'>
            <span className='font-semibold text-xl'>
              Location:{" "}
              <span className='text-sm font-bold text-gray-600 mx-4'>
                {location}
              </span>
            </span>
          </div>

          <div className=''>
            <span className='font-semibold text-xl'>
              Contact:{" "}
              <span className='text-sm font-bold text-gray-600 mx-4'>{contact}</span>
            </span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className='flex flex-col justify-center items-center'>
      <div className=' max-w-6xl w-full  my-16 h-screen flex flex-col justify-center items-center'>
        <div className='grid-2  w-full py-12'>
          <div className=' border-r border-gray-500'>
            <p className='text-4xl mx-2 font-bold'>Product Details</p>
            {details()}
          </div>

          <div className=''>
            <p className='text-4xl mx-2 font-bold text-gray-600'>Seller Details</p>
            {sellerDetails()}
          </div>
        </div>
      </div>
    </div>
  );
};

view.getInitialProps = (client) => {
  const prod = Product.find((prod) => prod.id === client.query.view);

  return { prod };
};

export default view;
